import React, { useState, useEffect } from 'react';
import { getAdminDashboardStats, createUser } from '../services/api';
import { UserPlus, FileText, Send } from 'lucide-react';
import Modal from '../components/Modal';

const NotificationIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none"
         stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"
         className="lucide lucide-bell">
        <path d="M6 8a6 6 0 0 1 12 0c0 7 3 9 3 9H3s3-2 3-9" />
        <path d="M10.3 21a1.94 1.94 0 0 0 3.4 0" />
    </svg>
);

const AdminDashboardPage = () => {
    const [stats, setStats] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [, setError] = useState(null);
    const [isAddUserOpen, setIsAddUserOpen] = useState(false);

    const [newUser, setNewUser] = useState({
        username: '',
        email: '',
        password: '',
        role: 'ROLE_EMPLOYEE'
    });

    useEffect(() => {
        const fetchStats = async () => {
            try {
                const response = await getAdminDashboardStats();
                setStats(response.data);
            } catch (err) {
                console.error("Failed to fetch admin dashboard stats:", err);
                setError('Failed to load dashboard data. Please try again.');
            } finally {
                setIsLoading(false);
            }
        };
        fetchStats();
    }, []);

    if (isLoading) {
        return (
            <div className="flex justify-center items-center h-screen bg-gray-100">
                <div className="text-xl font-semibold text-gray-700">Loading Dashboard...</div>
            </div>
        );
    }

    return (
        <div className="p-6">
            {/* BUTTONS */}
            <div className="flex flex-wrap gap-4 items-center justify-between mb-8">
                <h1 className="text-3xl font-bold text-gray-800 w-full sm:w-auto">Admin Dashboard</h1>
                <div className="flex flex-wrap gap-4">
                    <button className="relative text-gray-600 hover:text-gray-800">
                        <NotificationIcon />
                        <span className="absolute -top-2 -right-2 inline-flex items-center justify-center px-2 py-1 text-xs font-bold text-white bg-red-600 rounded-full">3</span>
                    </button>

                    <button
                        onClick={() => setIsAddUserOpen(true)}
                        className="flex items-center px-4 py-2 text-sm font-semibold text-white bg-blue-600 rounded-lg shadow-md hover:bg-blue-700"
                    >
                        <UserPlus className="w-4 h-4 mr-2" /> Add User
                    </button>

                    <button
                        className="flex items-center px-4 py-2 text-sm font-semibold text-blue-600 bg-white border border-blue-600 rounded-lg shadow-md hover:bg-gray-50"
                    >
                        <FileText className="w-4 h-4 mr-2" /> Export
                    </button>

                    <button className="flex items-center px-4 py-2 text-sm font-semibold text-white bg-green-600 rounded-lg shadow-md hover:bg-green-700">
                        <Send className="w-4 h-4 mr-2" /> Announce
                    </button>
                </div>
            </div>

            {/* STATS CARDS */}
            <div className="flex flex-wrap gap-6 justify-between mb-8">
                {stats && (
                    <>
                        <Card title="TOTAL HRS" value={stats.totalHr} activeCount={stats.activeHr} inactiveCount={stats.totalHr - stats.activeHr} color="bg-yellow-50" />
                        <Card title="TOTAL MANAGERS" value={stats.totalManagers} activeCount={stats.activeManagers} inactiveCount={stats.totalManagers - stats.activeManagers} color="bg-green-50" />
                        <Card title="TOTAL EMPLOYEES" value={stats.totalEmployees} activeCount={stats.activeEmployees} inactiveCount={stats.totalEmployees - stats.activeEmployees} color="bg-blue-50" />
                        <Card title="PENDING ONBOARDINGS" value={stats.pendingOnboardings} subtext="Last 2 days ago" color="bg-red-50" />
                    </>
                )}
            </div>

            {/* RECENT LOGINS */}
            <div className="bg-white rounded-xl shadow-md p-6">
                <h2 className="text-xl font-bold text-gray-800 mb-4">Recent Logins</h2>
                <div className="flex flex-col space-y-4">
                    <div className="bg-gray-50 p-4 rounded-lg flex items-center justify-between shadow-sm">
                        <div className="flex items-center space-x-3">
                            <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center text-blue-600 font-bold">JD</div>
                            <div>
                                <p className="font-semibold text-gray-800">John Doe</p>
                                <p className="text-sm text-gray-500">hr@payflow.com</p>
                            </div>
                        </div>
                        <div className="text-sm text-gray-500">Just now</div>
                    </div>
                </div>
            </div>

            {/* ADD USER MODAL */}
            <Modal isOpen={isAddUserOpen} onClose={() => setIsAddUserOpen(false)}>
                <h2 className="text-lg font-semibold mb-4">Add New User</h2>
                <form
                    onSubmit={async (e) => {
                        e.preventDefault();
                        try {
                            await createUser(newUser);
                            alert("User created successfully!");
                            setIsAddUserOpen(false);
                            setNewUser({ username: '', email: '', password: '', role: 'ROLE_EMPLOYEE' });
                        } catch (error) {
                            alert("Failed to create user. Check console.");
                            console.error(error);
                        }
                    }}
                    className="space-y-4"
                >
                    <input
                        type="text"
                        placeholder="Username"
                        className="w-full border px-3 py-2 rounded"
                        value={newUser.username}
                        onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                        required
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        className="w-full border px-3 py-2 rounded"
                        value={newUser.email}
                        onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        className="w-full border px-3 py-2 rounded"
                        value={newUser.password}
                        onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                        required
                    />
                    <select
                        value={newUser.role}
                        className="w-full border px-3 py-2 rounded"
                        onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
                    >
                        <option value="ROLE_HR">HR</option>
                        <option value="ROLE_MANAGER">Manager</option>
                        <option value="ROLE_EMPLOYEE">Employee</option>
                    </select>
                    <button
                        type="submit"
                        className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
                    >
                        Create User
                    </button>
                </form>
            </Modal>
        </div>
    );
};

const Card = ({ title, value, activeCount, inactiveCount, subtext, color }) => {
    return (
        <div className={`flex-1 min-w-[250px] rounded-xl shadow-md p-6 transform transition-transform hover:scale-[1.02] ${color}`}>
            <h3 className="text-sm font-semibold uppercase tracking-wider mb-1">{title}</h3>
            <p className="text-4xl font-bold text-gray-800 mb-1">{value}</p>
            {activeCount !== undefined && inactiveCount !== undefined ? (
                <p className="text-sm text-gray-600">
                    <span className="text-green-600 font-semibold">Active: {activeCount}</span>{' '}
                    <span className="ml-2 text-red-600 font-semibold">Inactive: {inactiveCount}</span>
                </p>
            ) : (
                <p className="text-sm text-gray-500">{subtext}</p>
            )}
        </div>
    );
};

export default AdminDashboardPage;
