import React, { useState, useEffect, useCallback } from 'react';
import Modal from 'react-modal';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

import { getManageableUsers, updateUserStatus, createUser } from '../services/api';
import '../components/Modal.css';
import '../components/Table.css';

const MySwal = withReactContent(Swal);
Modal.setAppElement('#root');

const ManageUsersPage = ({ user }) => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [newUser, setNewUser] = useState({ username: '', email: '', password: '', role: 'ROLE_HR' });

    const [currentPage, setCurrentPage] = useState(0);
    const [totalPages, setTotalPages] = useState(0);
    const [searchQuery, setSearchQuery] = useState('');
    const pageSize = 5;

    const fetchUsers = useCallback(() => {
        setIsLoading(true);
        getManageableUsers(currentPage, pageSize, searchQuery)
            .then(response => {
                setUsers(response.data.content);
                setTotalPages(response.data.totalPages);
            })
            .catch(() => {
                setError('Failed to load users. Please try again later.');
            })
            .finally(() => setIsLoading(false));
    }, [currentPage, pageSize, searchQuery]);

    useEffect(() => {
        fetchUsers();
    }, [fetchUsers]);

    const handleStatusToggle = (user) => {
        const newStatus = !user.enabled;
        const actionText = newStatus ? 'Enable' : 'Disable';

        MySwal.fire({
            title: `Confirm Action`,
            html: `Are you sure you want to <strong>${actionText}</strong> the user "<strong>${user.username}</strong>"?`,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: `Yes, ${actionText} it!`,
            cancelButtonText: 'No, cancel',
            confirmButtonColor: newStatus ? '#28a745' : '#dc3545',
            cancelButtonColor: '#6c757d',
            buttonsStyling: false,
        }).then((result) => {
            if (result.isConfirmed) {
                updateUserStatus(user.id, newStatus)
                    .then(() => {
                        MySwal.fire(
                            `${actionText}d!`,
                            `User "${user.username}" has been successfully ${actionText.toLowerCase()}d.`,
                            'success'
                        );
                        fetchUsers();
                    })
                    .catch(() => {
                        MySwal.fire('Action Failed!', 'Could not update the user status.', 'error');
                    });
            }
        });
    };

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => {
        setIsModalOpen(false);
        setNewUser({ username: '', email: '', password: '', role: 'ROLE_HR' });
    };

    const handleNewUserChange = (e) => {
        const { name, value } = e.target;
        setNewUser(prev => ({ ...prev, [name]: value }));
    };

    const handleCreateUserSubmit = (e) => {
        e.preventDefault();

        createUser(newUser)
            .then(() => {
                closeModal();
                MySwal.fire({
                    icon: 'success',
                    title: 'User Created!',
                    text: `The account for "${newUser.username}" has been successfully created.`,
                    timer: 2500,
                    showConfirmButton: false,
                });
                fetchUsers();
            })
            .catch(err => {
                let errorMessage = 'An unknown error occurred. Please check the details and try again.';
                if (err.response?.data?.message) errorMessage = err.response.data.message;
                else if (err.message) errorMessage = err.message;

                MySwal.fire({ icon: 'error', title: 'Creation Failed', text: errorMessage });
            });
    };

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
        setCurrentPage(0);
    };

    const goToPage = (pageNum) => {
        if (pageNum >= 0 && pageNum < totalPages) setCurrentPage(pageNum);
    };

    if (isLoading) return <p>Loading users...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-3">
                <h2 className="m-0">Manage Users</h2>
                <input
                    type="text"
                    placeholder="Search by name/email"
                    className="form-control w-50 me-2"
                    value={searchQuery}
                    onChange={handleSearchChange}
                />
                {user?.roles?.includes('ROLE_ADMIN') && (
                    <button onClick={openModal} className="btn btn-primary">+ Create User</button>
                )}
            </div>

            <table className="app-table">
                <thead>
                <tr>
                    <th>Name</th><th>Email</th><th>Role</th><th>Status</th><th>Action</th>
                </tr>
                </thead>
                <tbody>
                {users.map(user => (
                    <tr key={user.id}>
                        <td>{user.username}</td>
                        <td>{user.email || 'N/A'}</td>
                        <td>{user.role.replace('ROLE_', '')}</td>
                        <td style={{ fontWeight: 'bold', color: user.enabled ? '#28a745' : '#dc3545' }}>
                            {user.enabled ? 'Active' : 'Disabled'}
                        </td>
                        <td>
                            <button onClick={() => handleStatusToggle(user)} className={`btn ${user.enabled ? 'btn-danger' : 'btn-success'}`}>
                                {user.enabled ? 'Disable' : 'Enable'}
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

            {/* Pagination */}
            {totalPages > 1 && (
                <div className="d-flex justify-content-center mt-4 flex-wrap gap-2">
                    <button className="btn btn-outline-secondary" disabled={currentPage === 0} onClick={() => goToPage(0)}>First</button>

                    {Array.from({ length: totalPages }, (_, index) => (
                        <button
                            key={index}
                            className={`btn ${index === currentPage ? 'btn-primary' : 'btn-outline-primary'}`}
                            onClick={() => goToPage(index)}
                        >
                            {index + 1}
                        </button>
                    ))}

                    <button className="btn btn-outline-secondary" disabled={currentPage === totalPages - 1} onClick={() => goToPage(totalPages - 1)}>Last</button>
                </div>
            )}

            {/* Create User Modal */}
            <Modal isOpen={isModalOpen} onRequestClose={closeModal} className="modal-content" overlayClassName="modal-overlay">
                <h2>Create New User</h2>
                <button onClick={closeModal} className="modal-close-button">×</button>
                <form onSubmit={handleCreateUserSubmit}>
                    <div className="form-group"><label>Username</label><input type="text" name="username" value={newUser.username} onChange={handleNewUserChange} required /></div>
                    <div className="form-group"><label>Email</label><input type="email" name="email" value={newUser.email} onChange={handleNewUserChange} required /></div>
                    <div className="form-group"><label>Password</label><input type="password" name="password" value={newUser.password} onChange={handleNewUserChange} required minLength="6" /></div>
                    <div className="form-group"><label>Role</label>
                        <select name="role" value={newUser.role} onChange={handleNewUserChange}>
                            <option value="ROLE_HR">HR</option>
                            <option value="ROLE_MANAGER">Manager</option>
                        </select>
                    </div>
                    <button type="submit" className="btn btn-success w-100">Submit</button>
                </form>
            </Modal>
        </div>
    );
};

export default ManageUsersPage;
