// src/pages/ManageUsersPage.jsx

import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

import {
    getManageableUsers,
    updateUserStatus,
    createUser,
} from '../services/api';

import '../components/Modal.css';
import '../components/Table.css';

const MySwal = withReactContent(Swal);
Modal.setAppElement('#root');

const ManageUsersPage = ({ user }) => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [newUser, setNewUser] = useState({
        username: '',
        email: '',
        password: '',
        role: 'ROLE_HR',
    });

    const [currentPage, setCurrentPage] = useState(0);
    const [totalPages, setTotalPages] = useState(0);
    const [search, setSearch] = useState('');
    const pageSize = 5;

    const fetchUsers = () => {
        setIsLoading(true);
        getManageableUsers({ page: currentPage, size: pageSize, search })
            .then((response) => {
                setUsers(response.data.content);
                setTotalPages(response.data.totalPages);
            })
            .catch(() => {
                setError('Failed to load users.');
            })
            .finally(() => setIsLoading(false));
    };

    useEffect(() => {
        fetchUsers();
    }, [currentPage, search]);

    const handleStatusToggle = (user) => {
        const newStatus = !user.enabled;
        const actionText = newStatus ? 'Enable' : 'Disable';

        MySwal.fire({
            title: 'Confirm Action',
            html: `Are you sure you want to <strong>${actionText}</strong> the user "<strong>${user.username}</strong>"?`,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: `Yes, ${actionText} it!`,
            cancelButtonText: 'No',
        }).then((result) => {
            if (result.isConfirmed) {
                updateUserStatus(user.id, newStatus).then(() => {
                    MySwal.fire('Success!', `User "${user.username}" updated.`, 'success');
                    fetchUsers();
                });
            }
        });
    };

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => {
        setIsModalOpen(false);
        setNewUser({ username: '', email: '', password: '', role: 'ROLE_HR' });
    };

    const handleCreateUserSubmit = (e) => {
        e.preventDefault();
        createUser(newUser)
            .then(() => {
                MySwal.fire('Created!', 'User created successfully.', 'success');
                closeModal();
                fetchUsers();
            })
            .catch(() => {
                MySwal.fire('Error', 'Failed to create user.', 'error');
            });
    };

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-3">
                <h2>Manage Users</h2>
                <div className="d-flex align-items-center gap-3">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search by username or email"
                        style={{ maxWidth: '250px' }}
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                    />
                    {user?.roles?.includes('ROLE_ADMIN') && (
                        <button onClick={openModal} className="btn btn-primary">
                            + Create New User
                        </button>
                    )}
                </div>
            </div>

            {isLoading ? (
                <p>Loading users...</p>
            ) : error ? (
                <p className="text-danger">{error}</p>
            ) : (
                <>
                    <table className="table table-striped">
                        <thead>
                        <tr>
                            <th>Name</th><th>Email</th><th>Role</th><th>Status</th><th>Action</th>
                        </tr>
                        </thead>
                        <tbody>
                        {users.map((u) => (
                            <tr key={u.id}>
                                <td>{u.username}</td>
                                <td>{u.email}</td>
                                <td>{u.role.replace('ROLE_', '')}</td>
                                <td>
                    <span className={u.enabled ? 'text-success fw-bold' : 'text-danger fw-bold'}>
                      {u.enabled ? 'Active' : 'Disabled'}
                    </span>
                                </td>
                                <td>
                                    <button
                                        onClick={() => handleStatusToggle(u)}
                                        className={`btn btn-sm ${u.enabled ? 'btn-danger' : 'btn-success'}`}
                                    >
                                        {u.enabled ? 'Disable' : 'Enable'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>

                    {/* Pagination Controls */}
                    {totalPages > 1 && (
                        <div className="d-flex justify-content-center align-items-center gap-2 my-3 flex-wrap">
                            <button
                                className="btn btn-outline-secondary"
                                onClick={() => setCurrentPage(0)}
                                disabled={currentPage === 0}
                            >
                                First
                            </button>
                            <button
                                className="btn btn-outline-secondary"
                                onClick={() => setCurrentPage((prev) => prev - 1)}
                                disabled={currentPage === 0}
                            >
                                Prev
                            </button>
                            {[...Array(totalPages).keys()].map((page) => (
                                <button
                                    key={page}
                                    className={`btn ${page === currentPage ? 'btn-primary' : 'btn-outline-primary'}`}
                                    onClick={() => setCurrentPage(page)}
                                >
                                    {page + 1}
                                </button>
                            ))}
                            <button
                                className="btn btn-outline-secondary"
                                onClick={() => setCurrentPage((prev) => prev + 1)}
                                disabled={currentPage === totalPages - 1}
                            >
                                Next
                            </button>
                            <button
                                className="btn btn-outline-secondary"
                                onClick={() => setCurrentPage(totalPages - 1)}
                                disabled={currentPage === totalPages - 1}
                            >
                                Last
                            </button>
                        </div>
                    )}
                </>
            )}

            {/* Create User Modal */}
            <Modal isOpen={isModalOpen} onRequestClose={closeModal} className="modal-content" overlayClassName="modal-overlay">
                <h4>Create New User</h4>
                <form onSubmit={handleCreateUserSubmit}>
                    <div className="form-group mb-2">
                        <label>Username</label>
                        <input type="text" className="form-control" required value={newUser.username} onChange={(e) => setNewUser({ ...newUser, username: e.target.value })} />
                    </div>
                    <div className="form-group mb-2">
                        <label>Email</label>
                        <input type="email" className="form-control" value={newUser.email} onChange={(e) => setNewUser({ ...newUser, email: e.target.value })} />
                    </div>
                    <div className="form-group mb-2">
                        <label>Password</label>
                        <input type="password" className="form-control" required value={newUser.password} onChange={(e) => setNewUser({ ...newUser, password: e.target.value })} />
                    </div>
                    <div className="form-group mb-3">
                        <label>Role</label>
                        <select className="form-control" value={newUser.role} onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}>
                            <option value="ROLE_HR">HR</option>
                            <option value="ROLE_MANAGER">Manager</option>
                            <option value="ROLE_EMPLOYEE">Employee</option>
                        </select>
                    </div>
                    <button type="submit" className="btn btn-primary w-100">Create User</button>
                </form>
            </Modal>
        </div>
    );
};

export default ManageUsersPage;
